package Java12;

public class OneToTen {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		for (int i = 0; i <=10; i++) {
			System.out.print(i);
			
		}
		for(int i=0;i<=10;i++){
			if(i%2==0){
				System.out.println("Even numbers is = "+i);
			}
		}
		for(int i=0;i<=10;i++){
			if(i%2!=0){
				System.out.println("Odd numbers is = "+i);
			}
		}
	}

}
