package NewJava;

public class EvenAndOddNumbers {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		for (int i = 0; i <=10; i++) {
			if(i%2==0){
				System.out.println("Even numbers="+i);
			}
			else {
				System.out.println("Odd numbers= "+i);
			}
			
		}
	}

}
